INSERT INTO public.questions (id, question_type, difficulty, topic, question, answer, quiz, match_keywords, is_active, created_at, updated_at) VALUES (5, 'text_input', 'easy', 'REST', '<p>Which&nbsp;<strong>HTTP&nbsp;request&nbsp;methods</strong>&nbsp;are&nbsp;used&nbsp;to&nbsp;manipulate&nbsp;resources&nbsp;in&nbsp;a&nbsp;RESTful&nbsp;API?</p>', '<ul><li><strong>GET</strong>:&nbsp;receive&nbsp;a&nbsp;resource</li><li><strong>POST</strong>:&nbsp;create&nbsp;a&nbsp;new&nbsp;resource</li><li><strong>PUT</strong>:&nbsp;replace&nbsp;a&nbsp;resource</li><li><strong>PATCH</strong>:&nbsp;update&nbsp;a&nbsp;resource</li><li><strong>DELETE</strong>:&nbsp;delete&nbsp;a&nbsp;resource</li></ul>', NULL, '{GET,POST,PUT,PATCH,DELETE}', true, '2025-12-01 19:40:39.375932+00', '2025-12-03 12:44:05.956552+00');
INSERT INTO public.questions (id, question_type, difficulty, topic, question, answer, quiz, match_keywords, is_active, created_at, updated_at) VALUES (1, 'text_input', 'medium', 'TypeScript', '<p><span style="background-color: rgb(255, 255, 255);">What&nbsp;does&nbsp;an&nbsp;interface&nbsp;compile&nbsp;to&nbsp;in&nbsp;JavaScript?</span></p>', '<p><strong>Nothing</strong>.&nbsp;Interfaces&nbsp;exist&nbsp;only&nbsp;in&nbsp;TypeScript’s&nbsp;type&nbsp;system&nbsp;and&nbsp;are&nbsp;completely&nbsp;removed&nbsp;during&nbsp;compilation,&nbsp;so&nbsp;they&nbsp;produce&nbsp;no&nbsp;JavaScript&nbsp;output.</p>', NULL, '{nothing}', true, '2025-12-01 11:55:35.526087+00', '2025-12-01 21:57:20.662165+00');
