INSERT INTO public.questions (id, question_type, difficulty, topic, question, answer, quiz, match_keywords, is_active, created_at, updated_at) VALUES (58, 'multiple_choice', 'easy', 'HTTP', '<p>Which&nbsp;of&nbsp;the&nbsp;following&nbsp;are&nbsp;real&nbsp;<strong>benefits&nbsp;of&nbsp;HTTP</strong>?</p>', '<p><strong>Correct&nbsp;answers</strong>:</p><ul><li>Stateless&nbsp;application-layer&nbsp;protocol</li><li>Standard&nbsp;methods&nbsp;and&nbsp;status&nbsp;codes&nbsp;(GET,&nbsp;POST,&nbsp;PUT,&nbsp;DELETE)</li><li>Platform-independent&nbsp;and&nbsp;extensible&nbsp;(caching,&nbsp;headers,&nbsp;flexible&nbsp;formats)</li></ul><p></p><p><strong>Wrong&nbsp;answers</strong>:</p><ul><li>Built-in&nbsp;automatic&nbsp;encryption</li><li>Requires&nbsp;long-lived&nbsp;TCP&nbsp;sessions</li><li>Forces&nbsp;XML&nbsp;as&nbsp;the&nbsp;only&nbsp;data&nbsp;format</li><li>Only&nbsp;works&nbsp;on&nbsp;port&nbsp;80</li><li>Tightly&nbsp;couples&nbsp;client&nbsp;and&nbsp;server&nbsp;OS</li><li>Requires&nbsp;server-side&nbsp;sessions&nbsp;for&nbsp;every&nbsp;interaction</li></ul>', NULL, '{}', true, '2025-12-05 11:26:58.327652+00', '2025-12-05 11:26:58.333751+00');
INSERT INTO public.questions (id, question_type, difficulty, topic, question, answer, quiz, match_keywords, is_active, created_at, updated_at) VALUES (27, 'multiple_choice', 'easy', 'Design patterns', '<p>What&nbsp;<strong>creational</strong>&nbsp;design&nbsp;patterns&nbsp;do&nbsp;you&nbsp;know?</p>', '<p>Creational&nbsp;patterns&nbsp;help&nbsp;control&nbsp;object&nbsp;creation</p><ul><li><strong>Singleton</strong>&nbsp;(single&nbsp;instance)</li><li><strong>Factory</strong>&nbsp;+&nbsp;<strong>Abstract&nbsp;Factory</strong>&nbsp;(controlled&nbsp;creation)</li><li><strong>Builder</strong>&nbsp;(stepwise&nbsp;construction)</li><li><strong>Prototype</strong>&nbsp;(cloning).</li></ul>', NULL, '{}', true, '2025-12-03 13:25:51.365146+00', '2025-12-03 13:37:40.684609+00');
