INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2319, 53, 'JWT-based systems are stateless because the server only needs a secret or public key to verify incoming tokens.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2320, 53, 'A JWT stores user passwords in encrypted form inside the payload to allow servers to re-authenticate users.', false, NULL);
