INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2577, 69, 'Node.js event loop contains multiple structured phases (timers, pending callbacks, poll, check, close callbacks) that do not exist in browser environments.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2578, 69, 'Browser event loops include a rendering step, whereas Node.js does not perform rendering and therefore has no rendering phase.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2579, 69, 'Node.js adds system-level capabilities (file I/O, TCP, DNS, etc.) to the event loop through libuv, while browsers delegate such operations to Web APIs or do not support them.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2580, 69, 'Browsers run microtasks only after rendering completes, while Node.js runs microtasks only before rendering begins.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2581, 69, 'The Node.js event loop is single-phased and strictly identical to the browser event loop, differing only in available global objects.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2582, 69, 'Browser event loops allow JavaScript to perform synchronous filesystem I/O during the poll phase, but Node.js forbids all filesystem access inside the event loop.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2583, 69, 'Node.js executes timers (setTimeout, setInterval) strictly at the scheduled millisecond mark, while browsers may delay timers but Node never does.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2584, 69, 'In browsers, microtasks can be postponed until after several macrotasks, while Node.js forces microtasks to run only at the end of the poll phase.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2325, 54, 'The JWT signature ensures the token’s integrity by allowing the server to verify that the token has not been tampered with and was issued by a trusted party.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2326, 54, 'The JWT signature encrypts the entire token so that the payload cannot be read by the client.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2327, 54, 'The JWT signature stores the user’s password to confirm identity without database lookups.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2328, 54, 'The JWT signature is used to automatically refresh tokens when they expire, without contacting the server.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2329, 54, 'The JWT signature allows clients to modify claims safely, because the signature will regenerate itself.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2330, 54, 'The JWT signature determines the user’s role and permissions inside the application.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2331, 54, 'The JWT signature hides sensitive fields like email or ID so attackers cannot decode them.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2332, 54, 'The JWT signature ensures that only the client can verify the token, preventing the server from validating it.', false, NULL);
