INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2349, 52, 'JWT is a token format that defines how claims are encoded and signed, while OAuth2 is an authorization framework that defines how clients obtain and use access tokens, and can use many token formats including JWT.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2350, 52, 'JWT is a complete authentication protocol that replaces OAuth2 and handles login, consent, and authorization scopes on its own.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2351, 52, 'OAuth2 is simply a token format for embedding user data, while JWT is the system that provides authorization flows like authorization code or client credentials.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2352, 52, 'JWT always requires a centralized authorization server, while OAuth2 can operate without one.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2353, 52, 'OAuth2 tokens must always be JWTs, because the framework mandates signed JSON tokens for every flow.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2354, 52, 'JWT is used only for refresh tokens, and OAuth2 is used only for access tokens.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2355, 52, 'OAuth2 enforces a specific signing algorithm for all tokens, while JWT does not support signatures at all.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2356, 52, 'JWT automatically provides authorization and role management, while OAuth2 is only used to encrypt tokens.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2624, 72, 'Macrotasks represent operations such as timers and I/O events that execute one at a time in successive event-loop iterations.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2625, 72, 'Promise resolution handlers run as microtasks, giving them higher scheduling priority than timer callbacks.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2626, 72, 'A macrotask will not begin execution until the microtask queue has been fully drained for that iteration of the event loop.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2627, 72, 'Microtask execution ordering is deterministic: all queued microtasks are processed before the next macrotask is started.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2585, 70, 'libuv provides Node.js with a cross-platform event loop and abstracts OS-level differences between Unix-like systems and Windows.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2586, 70, 'libuv uses a thread pool for operations that cannot be performed asynchronously at the OS level, such as filesystem access.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2587, 70, 'libuv manages timers, I/O polling, callbacks, and close events through a structured sequence of event-loop phases.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2588, 70, 'TCP/UDP sockets, pipes, TTY handling, and asynchronous DNS lookups in Node.js are powered by libuv.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2589, 70, 'libuv enables Node.js to perform asynchronous operations even when the underlying operating system only supports synchronous APIs.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2590, 70, 'libuv executes all JavaScript code inside its worker threads, allowing Node.js to run JavaScript in parallel by default.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2591, 70, 'libuv provides a built-in garbage collector that works alongside V8 to manage memory and clean unreachable objects.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2592, 70, 'libuv exposes its API directly to JavaScript code, so developers can call libuv functions without any native bindings.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2593, 70, 'Major GC in V8 is triggered by libuv scheduling it as part of the event loop’s poll phase.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2594, 70, 'libuv creates a separate event loop instance for every Node.js module loaded via require/import.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2628, 72, 'Microtasks execute only after all macrotasks and rendering steps have completed, giving them the lowest scheduling priority in the event loop.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2629, 72, 'Macrotasks include operations such as promise callbacks and mutation observer notifications, which require immediate post-stack execution.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2630, 72, 'Microtasks are scheduled to run only during the poll phase of the event loop and cannot execute in other phases.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2631, 72, 'Macrotasks must complete before any microtask can be scheduled, preventing microtasks from accumulating during synchronous execution.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2632, 72, 'A microtask may be postponed across multiple macrotask cycles if the runtime determines that postponement improves throughput.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2413, 60, 'A development practice in which tests are written before the production code, following a cycle of writing a failing test, implementing the minimal code to pass it, and then refactoring.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2414, 60, 'A methodology that requires developers to fully implement all features first and then generate automated tests from the finished codebase using code-analysis tools.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2415, 60, 'A testing strategy in which all test cases must be executed manually before any code changes are allowed, ensuring developers validate behavior without automation.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2416, 60, 'A design technique that mandates creating a complete architectural blueprint and data model before writing any tests or application logic, emphasizing upfront planning.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2417, 60, 'A debugging approach where developers investigate runtime failures and turn those failures into system requirements, using logs instead of predefined tests to guide development.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2418, 60, 'A workflow in which production code and test code are written simultaneously in the same file, allowing the compiler to interleave logic and assertions during execution.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2419, 60, 'A performance-driven process that requires creating benchmarks before function', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2420, 60, 'A documentation-oriented method where the primary goal is to produce exhaustive test reports, and software is implemented afterward based on the structure of those reports.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2437, 59, 'A development approach that expresses software behavior in human-readable scenarios from the user’s perspective, ensuring tests describe expected behavior rather than implementation details.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2438, 59, 'A methodology where developers write low-level unit tests before any requirements are gathered, focusing on internal algorithmic details rather than user outcomes.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2439, 59, 'A process model that replaces developers with automated agents that infer system behavior directly from database schemas and generate code without human involvement.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2440, 59, 'A design technique in which teams document architecture diagrams first, then translate those diagrams into executable test specifications without engaging stakeholders.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2441, 59, 'A testing strategy that requires all test cases to be written exclusively in XML and validated through schema enforcement, ensuring strict structural conformance.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2442, 59, 'A requirement-engineering method that mandates modeling systems with formal mathematical proofs to guarantee correctness before any functional behavior is discussed.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2443, 59, 'A workflow in which product owners write only UI wireframes, and developers convert those wireframes into automated tests without describing system behavior.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2444, 59, 'A debugging-focused development style that traces runtime logs to infer requirements, using those logs as the basis for defining tests and system expectations.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2453, 61, 'A software design approach that centers the implementation around the core business domain, using a shared ubiquitous language and domain models to ensure the code reflects real-world business concepts.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2454, 61, 'A deployment methodology in which applications are distributed across servers automatically based on CPU load, allowing the domain layer to scale independently of business rules.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2455, 61, 'A database-first strategy where developers generate all application code from schema diagrams, ensuring the structure of the database dictates the entire design of the system.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2456, 61, 'A visual modeling technique that focuses on producing exhaustive UML diagrams before writing code, ensuring the design is fully documented prior to implementation.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2457, 61, 'A project-management framework that replaces user stories with domain blueprints, instructing teams to plan all domain interactions upfront without iterative development.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2458, 61, 'A testing methodology in which every domain rule must be expressed as a unit test before any modeling decisions are made, making the test suite the primary source of requirements.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2459, 61, 'A performance-optimization practice where domains are divided strictly based on memory access patterns and caching strategies instead of business behavior.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2460, 61, 'A refactoring technique that restructures code according to low-level technical layers such as controllers, repositories, and utilities, without incorporating business concepts or stakeholder terminology.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2461, 62, 'In DDD-based systems with multiple bounded contexts, the Saga pattern coordinates long-running or distributed business transactions by orchestrating domain events or commands across services, ensuring eventual consistency where a single ACID transaction is not possible.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2462, 62, 'The Saga pattern replaces aggregates in DDD entirely, allowing domain models to be discarded in favor of centralized transaction controllers.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2463, 62, 'DDD requires every bounded context to include exactly one Saga, making Sagas the mandatory entry point for all commands in the domain layer.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2464, 62, 'The Saga pattern eliminates the need for domain events in DDD because it forces all services to share a single transactional database.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2465, 62, 'In DDD, Sagas act as repositories that store the full history of an aggregate’s state, allowing developers to rebuild domain objects without event sourcing.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2466, 62, 'Sagas are used in DDD to merge multiple bounded contexts into a single context, removing boundaries so transactions become simpler and fully synchronous.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2467, 62, 'The Saga pattern in DDD forces aggregates to expose their internal state directly so compensating actions can update them without going through domain rules.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2468, 62, 'DDD prohibits the use of Sagas because they introduce eventual consistency, which is incompatible with the strict transactional guarantees required in all bounded contexts.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2479, 63, 'TCP guarantees ordered, reliable delivery using acknowledgements and retransmission mechanisms.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2480, 63, 'UDP is connectionless and does not require a handshake, enabling faster transmission with lower overhead.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2481, 63, 'UDP is suitable for real-time use cases (gaming, VoIP, streaming) where occasional packet loss is acceptable but latency must be minimal.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2482, 63, 'TCP and UDP both use a handshake process to establish a stable, connection-oriented session before sending any data.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2483, 63, 'UDP guarantees delivery by storing packets until acknowledgements are received, ensuring no data is lost.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2484, 63, 'TCP is faster than UDP because it avoids overhead and skips retransmission logic to deliver packets immediately.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2485, 63, 'UDP enforces strict packet ordering, while TCP delivers packets in whatever order they arrive.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2486, 63, 'TCP is typically used for real-time applications like gaming and live voice because its reliability prevents delay.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2487, 63, 'UDP is a stateful protocol that tracks session metadata for each packet exchange, while TCP is stateless.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2488, 63, 'TCP supports broadcast and multicast communication, allowing data to reach multiple recipients, while UDP cannot.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2489, 64, 'A reverse proxy hides backend servers from clients and routes incoming requests to the appropriate internal service.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2490, 64, 'A reverse proxy can improve scalability through load balancing across multiple backend servers.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2491, 64, 'A reverse proxy can handle SSL termination, offloading encryption work from backend services.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2492, 64, 'A reverse proxy is a client-side tool that rewrites outgoing HTTP requests before they reach the internet, used mostly in browsers for modifying URLs.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2493, 64, 'A reverse proxy stores full copies of backend applications and automatically updates them when clients send new requests.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2494, 64, 'A reverse proxy initiates outbound connections to fetch content on behalf of servers, functioning as a secure tunnel for server-to-client communication.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2495, 64, 'Reverse proxies are required only when using IPv6, because backend servers cannot communicate directly with IPv4 clients.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2496, 64, 'A reverse proxy replaces DNS entirely by assigning dynamic hostnames to backend servers during request forwarding.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2497, 64, 'Reverse proxies guarantee end-to-end encryption between clients and backend services without needing TLS certificates on the proxy itself.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2498, 64, 'A reverse proxy must run on the same host as the backend service to ensure minimal latency and direct file access.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2499, 65, 'Node.js applications often use in-memory caches (e.g., Map, LRU cache libraries) for fast, process-local lookups.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2500, 65, 'External cache systems like Redis or Memcached allow sharing cached data across multiple Node.js instances.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2501, 65, 'Caching reduces expensive repeated operations, improving throughput and lowering response times.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2502, 65, 'Node.js caches modules after the first require/import, returning the same instance on subsequent loads and effectively making modules singletons.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2503, 65, 'Node.js caching automatically persists all cached values to disk without any configuration, guaranteeing data survival across restarts.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2504, 65, 'Using caching in Node.js removes the need for a database entirely, since cached values permanently replace backend data sources.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2505, 65, 'Node.js requires built-in native caching APIs to perform any caching, because JavaScript objects cannot store key-value pairs efficiently.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2506, 65, 'Every Node.js worker thread automatically shares the same in-memory cache, ensuring perfect consistency across the application.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2507, 65, 'Caching in Node.js guarantees strong consistency, ensuring the cache is always synchronized with the database.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2508, 65, 'Node.js caching is only useful for static files because dynamic data cannot be cached safely in JavaScript applications.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2509, 65, 'Node.js must invalidate all cached items automatically at fixed intervals, as user-defined expiration policies are not supported.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2595, 71, 'process.nextTick() is a Node.js mechanism that schedules a callback to run immediately after the current JavaScript execution stack completes, before the event loop proceeds to the next phase, effectively giving it priority over other queued work.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2596, 71, 'process.nextTick() is a scheduling API that defers a callback to the next event-loop phase after all macrotasks and microtasks have been processed, ensuring it runs at the lowest priority.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2597, 71, 'process.nextTick() is a Node.js timer primitive that guarantees a callback executes after a minimum delay determined by the runtime’s internal scheduling interval.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2598, 71, 'process.nextTick() is a mechanism that queues a callback for execution exclusively during the poll phase, ensuring it runs after all pending I/O operations.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2599, 71, 'process.nextTick() is a concurrency construct that dispatches callbacks to worker threads so they run independently of the main event loop.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2600, 71, 'process.nextTick() is a browser-level microtask API standardized for Web APIs, enabling consistent scheduling behavior across client-side JavaScript environments.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2601, 71, 'process.nextTick() is a memory-management hook that defers execution of a callback until after V8 completes its next garbage-collection cycle.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2602, 71, 'process.nextTick() is a startup routine that runs callbacks once during process initialization, before user scripts and modules are executed.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2633, 73, 'The Reactor pattern uses a non-blocking event demultiplexer (such as epoll, kqueue, or IOCP) to wait for I/O readiness and dispatch events to handlers.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2634, 73, 'A Reactor manages many concurrent connections on a single thread by invoking handlers when events become ready rather than spinning up threads.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2635, 73, 'The Reactor pattern separates event detection from event handling, allowing the event loop to multiplex many I/O sources efficiently.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2521, 66, 'The Node.js cluster module enables creating multiple worker processes so applications can use more than one CPU core.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2522, 66, 'Each cluster worker is a separate Node.js process with its own event loop and memory space.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2523, 66, 'A master process can distribute incoming connections across workers, improving scalability and throughput.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2524, 66, 'Workers can exit or crash independently, and the master can respawn them to maintain service availability.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2525, 66, 'Node.js clustering merges multiple CPU cores into a single event loop, allowing one process to run code in true parallel threads.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2526, 66, 'Clustering automatically shares in-memory caches between workers, guaranteeing consistent state across processes.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2527, 66, 'A cluster worker can access another worker’s variables directly, since all workers run inside the same memory space.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2528, 66, 'Clustering eliminates the need for load balancers, because it prevents any worker from becoming overloaded.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2529, 66, 'Node.js clustering allows workers to execute synchronous CPU-bound operations without impacting overall performance.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2530, 66, 'Cluster workers always restart automatically when they exit, without requiring any configuration in the master process.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2531, 66, 'Clustering is unnecessary in modern Node.js because the runtime automatically uses all CPU cores without any manual setup.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2636, 73, 'Network servers like Node.js (via libuv) and Nginx use the Reactor pattern to achieve scalable, asynchronous I/O without blocking.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2637, 73, 'The Reactor pattern handles readiness-based events (e.g., readable or writable sockets), invoking callbacks only when operations can proceed without blocking.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2638, 73, 'The Reactor pattern spawns a new operating-system thread for every incoming connection, allowing each handler to execute independently.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2639, 73, 'The Reactor pattern processes all I/O synchronously, blocking until each handler finishes before checking for new events.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2640, 73, 'The Reactor pattern relies on polling all file descriptors in a tight loop without waiting for readiness notifications.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2641, 73, 'In the Reactor pattern, handlers initiate I/O directly without the reactor’s involvement, making the event loop optional.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2642, 73, 'The Reactor pattern guarantees that each event handler runs in parallel across multiple CPU cores by default.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2554, 67, 'Node.js reads cookies from the Cookie header, which contains key–value pairs sent by the client with each request.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2555, 67, 'Cookies are set in Node.js by sending one or more Set-Cookie headers in the HTTP response.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2556, 67, 'Middleware such as cookie-parser in Express can automatically parse, populate, and optionally sign cookies for secure use.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2557, 67, 'Cookies can be configured with attributes like HttpOnly, Secure, SameSite, and expiration to control security and lifetime.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2558, 67, 'Node.js automatically encrypts all cookies before sending them to clients, ensuring they cannot be read or modified.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2559, 67, 'Cookies in Node.js are stored in a built-in server-side cookie database that persists values across restarts.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2560, 67, 'Setting a cookie in Node.js requires a persistent WebSocket connection so the server can transmit cookie metadata.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2561, 67, 'Node.js merges cookies from all clients into one shared memory object for easier global access.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2562, 67, 'Express automatically deletes expired cookies from the browser without needing any configuration.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2563, 67, 'Cookies must be JSON-encoded objects in Node.js because plain string values are not supported by HTTP.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2564, 67, 'Node.js can only send one cookie per response because multiple Set-Cookie headers will overwrite each other.', false, NULL);
