INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2727, 75, 'Middleware makes it possible to extract commonly used behaviors into standalone functions so the same logic can be applied across multiple routes instead of being duplicated.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2728, 75, 'By placing shared responsibilities in middleware, route handlers become simpler and more focused on the specific task they perform.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2729, 75, 'Middleware components can interpret or adjust information on the request or response objects before they reach the endpoint that finalizes the operation.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2730, 75, 'A dedicated middleware designed for handling failures can intercept errors from anywhere in the pipeline and produce consistent responses.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2731, 75, 'Multiple middleware functions can be arranged in a chain, allowing complex behavior to be built from smaller steps executed in a predictable order.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2732, 75, 'Middleware automatically parallelizes all controller logic so that every request is handled using multiple CPU cores without developer configuration.', false, 'Middleware does not control CPU parallelism; Node.js remains single-threaded unless worker threads are used.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2733, 75, 'Middleware guarantees that all network responses are delayed until the full middleware sequence finishes, preventing early termination.', false, 'A middleware function can end the response at any stage; it does not enforce completion of the entire chain.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2734, 75, 'Middleware provides built-in storage that preserves request data between client visits without any external database.', false, 'Middleware does not persist request data; that requires explicit storage mechanisms.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2735, 75, 'Middleware automatically encrypts every response sent by the server and determines which cipher suites the client may use.', false, 'Middleware does not manage encryption by default; HTTPS/TLS handles that.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2736, 75, 'Middleware defines low-level transport mechanisms such as TCP packet handling, retransmission, and connection negotiation.', false, 'Middleware operates at the application layer, not at the transport or networking layer.');
