INSERT INTO public.questions (id, question_type, practical, difficulty, topic, question, answer, quiz, match_keywords, is_active, created_at, updated_at) VALUES (33, 'text_input', false, 'medium', 'Frontend', '<p>How&nbsp;do&nbsp;you&nbsp;call&nbsp;the&nbsp;browser&nbsp;mechanism&nbsp;that&nbsp;controls&nbsp;<strong>which&nbsp;external&nbsp;origins</strong>&nbsp;are&nbsp;allowed&nbsp;to&nbsp;access&nbsp;a&nbsp;website’s&nbsp;resources?</p>', '<p><strong>CORS</strong>&nbsp;(Cross-Origin&nbsp;Resource&nbsp;Sharing)&nbsp;is&nbsp;a&nbsp;security&nbsp;mechanism&nbsp;in&nbsp;browsers&nbsp;that&nbsp;determines&nbsp;whether&nbsp;one&nbsp;site&nbsp;is&nbsp;allowed&nbsp;to&nbsp;make&nbsp;requests&nbsp;to&nbsp;another&nbsp;origin.&nbsp;It&nbsp;prevents&nbsp;malicious&nbsp;websites&nbsp;from&nbsp;reading&nbsp;cross-origin&nbsp;responses&nbsp;unless&nbsp;explicitly&nbsp;permitted&nbsp;by&nbsp;the&nbsp;server.&nbsp;CORS&nbsp;uses&nbsp;HTTP&nbsp;headers&nbsp;like&nbsp;Access-Control-Allow-Origin&nbsp;to&nbsp;enforce&nbsp;these&nbsp;rules.&nbsp;It&nbsp;does&nbsp;not&nbsp;protect&nbsp;against&nbsp;CSRF&nbsp;or&nbsp;XSS&nbsp;directly;&nbsp;it&nbsp;only&nbsp;governs&nbsp;cross-origin&nbsp;reads.&nbsp;Proper&nbsp;configuration&nbsp;is&nbsp;essential&nbsp;to&nbsp;avoid&nbsp;accidentally&nbsp;exposing&nbsp;sensitive&nbsp;APIs.</p>', '', '{CORS}', true, '2025-12-03 14:06:14.155415+00', '2025-12-16 12:12:02.072733+00');
