INSERT INTO public.questions (id, question_type, practical, difficulty, topic, question, answer, quiz, match_keywords, is_active, created_at, updated_at) VALUES (60, 'single_choice', false, 'easy', 'Development Methodologies', '<p>What&nbsp;is&nbsp;Test&nbsp;Driven&nbsp;Development&nbsp;(<strong>TDD</strong>)?</p>', '<p>TDD&nbsp;(Test-Driven&nbsp;Development)&nbsp;is&nbsp;a&nbsp;development&nbsp;approach&nbsp;where&nbsp;you&nbsp;write&nbsp;tests&nbsp;first,&nbsp;then&nbsp;write&nbsp;just&nbsp;enough&nbsp;code&nbsp;to&nbsp;make&nbsp;those&nbsp;tests&nbsp;pass,&nbsp;and&nbsp;finally&nbsp;refactor&nbsp;the&nbsp;code&nbsp;while&nbsp;keeping&nbsp;the&nbsp;tests&nbsp;green.&nbsp;TDD&nbsp;leads&nbsp;to&nbsp;cleaner&nbsp;design,&nbsp;fewer&nbsp;bugs,&nbsp;and&nbsp;high&nbsp;confidence&nbsp;in&nbsp;code&nbsp;changes.</p>', NULL, '{}', true, '2025-12-05 11:35:02.006882+00', '2025-12-16 12:12:02.072733+00');
INSERT INTO public.questions (id, question_type, practical, difficulty, topic, question, answer, quiz, match_keywords, is_active, created_at, updated_at) VALUES (59, 'single_choice', false, 'medium', 'Development Methodologies', '<p>What&nbsp;is&nbsp;Behavioural&nbsp;Driven&nbsp;Development&nbsp;(<strong>BDD</strong>)?</p>', '<p>A&nbsp;development&nbsp;approach&nbsp;that&nbsp;expresses&nbsp;software&nbsp;behavior&nbsp;in&nbsp;human-readable&nbsp;scenarios&nbsp;from&nbsp;the&nbsp;user’s&nbsp;perspective,&nbsp;ensuring&nbsp;tests&nbsp;describe&nbsp;expected&nbsp;behavior&nbsp;rather&nbsp;than&nbsp;implementation&nbsp;details.</p>', NULL, '{}', true, '2025-12-05 11:33:04.736373+00', '2025-12-16 12:12:02.072733+00');
INSERT INTO public.questions (id, question_type, practical, difficulty, topic, question, answer, quiz, match_keywords, is_active, created_at, updated_at) VALUES (1, 'text_input', false, 'medium', 'TypeScript', '<p><span style="background-color: rgb(255, 255, 255);">What&nbsp;does&nbsp;an&nbsp;</span><strong style="background-color: rgb(255, 255, 255);">interface&nbsp;compile</strong><span style="background-color: rgb(255, 255, 255);">&nbsp;to&nbsp;in&nbsp;JavaScript?</span></p>', '<p><strong>Nothing</strong>.&nbsp;Interfaces&nbsp;exist&nbsp;only&nbsp;in&nbsp;TypeScript’s&nbsp;type&nbsp;system&nbsp;and&nbsp;are&nbsp;completely&nbsp;removed&nbsp;during&nbsp;compilation,&nbsp;so&nbsp;they&nbsp;produce&nbsp;no&nbsp;JavaScript&nbsp;output.</p>', '', '{nothing}', true, '2025-12-01 11:55:35.526087+00', '2025-12-16 12:12:02.072733+00');
INSERT INTO public.questions (id, question_type, practical, difficulty, topic, question, answer, quiz, match_keywords, is_active, created_at, updated_at) VALUES (24, 'single_choice', false, 'hard', 'Domain modeling', '<p>What&nbsp;is&nbsp;a&nbsp;<strong>Bounded&nbsp;context</strong>?</p>', '<p>A&nbsp;bounded&nbsp;context&nbsp;is&nbsp;a&nbsp;clear&nbsp;boundary&nbsp;in&nbsp;DDD&nbsp;where&nbsp;a&nbsp;particular&nbsp;domain&nbsp;model&nbsp;applies&nbsp;consistently.&nbsp;Inside&nbsp;the&nbsp;context,&nbsp;terms&nbsp;and&nbsp;rules&nbsp;have&nbsp;a&nbsp;precise&nbsp;meaning;&nbsp;outside,&nbsp;they&nbsp;may&nbsp;differ.&nbsp;It&nbsp;helps&nbsp;break&nbsp;large&nbsp;domains&nbsp;into&nbsp;smaller,&nbsp;well-defined&nbsp;parts&nbsp;that&nbsp;can&nbsp;evolve&nbsp;independently.</p>', '<p>Which&nbsp;of&nbsp;the&nbsp;following&nbsp;are&nbsp;true&nbsp;for&nbsp;<strong>Bounded&nbsp;Contexts</strong>?</p>', '{}', true, '2025-12-03 13:08:25.859503+00', '2025-12-16 12:12:02.072733+00');
