INSERT INTO public.questions (id, question_type, practical, difficulty, topic, question, answer, quiz, match_keywords, is_active, created_at, updated_at) VALUES (61, 'single_choice', false, 'medium', 'Development Methodologies', '<p>What&nbsp;is&nbsp;Domain&nbsp;Driven&nbsp;Development&nbsp;(<strong>DDD</strong>)?</p>', '<p>DDD&nbsp;(Domain-Driven&nbsp;Design)&nbsp;is&nbsp;an&nbsp;approach&nbsp;to&nbsp;software&nbsp;design&nbsp;where&nbsp;the&nbsp;core&nbsp;domain&nbsp;and&nbsp;its&nbsp;business&nbsp;logic&nbsp;drive&nbsp;the&nbsp;architecture.</p><p>The&nbsp;idea&nbsp;is&nbsp;to&nbsp;work&nbsp;closely&nbsp;with&nbsp;domain&nbsp;experts,&nbsp;create&nbsp;a&nbsp;ubiquitous&nbsp;language,&nbsp;and&nbsp;model&nbsp;the&nbsp;system&nbsp;around&nbsp;real&nbsp;business&nbsp;concepts&nbsp;rather&nbsp;than&nbsp;technical&nbsp;structures.</p><p>DDD&nbsp;helps&nbsp;build&nbsp;complex&nbsp;systems&nbsp;by&nbsp;keeping&nbsp;the&nbsp;software&nbsp;aligned&nbsp;with&nbsp;real-world&nbsp;business&nbsp;behavior&nbsp;and&nbsp;rules.</p><p></p><p><strong>Key&nbsp;concepts</strong>:</p><ul><li>Entities&nbsp;(objects&nbsp;with&nbsp;identity)</li><li>Value&nbsp;Objects&nbsp;(immutable&nbsp;descriptive&nbsp;types)</li><li>Aggregates&nbsp;(clusters&nbsp;of&nbsp;domain&nbsp;objects&nbsp;with&nbsp;rules)</li><li>Repositories&nbsp;(persistence&nbsp;abstractions)</li><li>Domain&nbsp;Events</li><li>Bounded&nbsp;Contexts&nbsp;(clear&nbsp;boundaries&nbsp;between&nbsp;subdomains)</li></ul>', NULL, '{}', true, '2025-12-05 11:37:08.055835+00', '2025-12-16 12:12:02.072733+00');
