INSERT INTO public.questions (id, question_type, practical, difficulty, topic, question, answer, quiz, match_keywords, is_active, created_at, updated_at) VALUES (31, 'text_input', false, 'medium', 'Frontend', '<p>How&nbsp;do&nbsp;you&nbsp;call&nbsp;the&nbsp;attack&nbsp;where&nbsp;a&nbsp;malicious&nbsp;site&nbsp;tricks&nbsp;a&nbsp;user’s&nbsp;browser&nbsp;into&nbsp;sending&nbsp;<strong>unwanted&nbsp;requests</strong>&nbsp;to&nbsp;a&nbsp;site&nbsp;where&nbsp;the&nbsp;user&nbsp;is&nbsp;authenticated?</p>', '<p><strong>CSRF</strong>&nbsp;(Cross-Site&nbsp;Request&nbsp;Forgery)&nbsp;is&nbsp;an&nbsp;attack&nbsp;where&nbsp;a&nbsp;malicious&nbsp;website&nbsp;tricks&nbsp;a&nbsp;user’s&nbsp;browser&nbsp;into&nbsp;sending&nbsp;unintended&nbsp;requests&nbsp;to&nbsp;a&nbsp;trusted&nbsp;site&nbsp;where&nbsp;the&nbsp;user&nbsp;is&nbsp;already&nbsp;authenticated.&nbsp;Because&nbsp;browsers&nbsp;automatically&nbsp;include&nbsp;cookies,&nbsp;tokens,&nbsp;or&nbsp;session&nbsp;data,&nbsp;the&nbsp;request&nbsp;appears&nbsp;legitimate&nbsp;to&nbsp;the&nbsp;target&nbsp;server.&nbsp;This&nbsp;allows&nbsp;attackers&nbsp;to&nbsp;perform&nbsp;actions&nbsp;such&nbsp;as&nbsp;changing&nbsp;account&nbsp;details,&nbsp;making&nbsp;purchases,&nbsp;or&nbsp;transferring&nbsp;funds&nbsp;without&nbsp;the&nbsp;user’s&nbsp;consent.&nbsp;CSRF&nbsp;targets&nbsp;state-changing&nbsp;operations,&nbsp;not&nbsp;data&nbsp;theft.&nbsp;Proper&nbsp;defenses&nbsp;include&nbsp;same-site&nbsp;cookies,&nbsp;CSRF&nbsp;tokens,&nbsp;and&nbsp;requiring&nbsp;explicit&nbsp;user&nbsp;interaction.</p>', '', '{CSRF}', true, '2025-12-03 14:02:33.741426+00', '2025-12-16 12:12:02.072733+00');
INSERT INTO public.questions (id, question_type, practical, difficulty, topic, question, answer, quiz, match_keywords, is_active, created_at, updated_at) VALUES (34, 'single_choice', false, 'easy', 'Security', '<p>What&nbsp;is&nbsp;the&nbsp;difference&nbsp;between&nbsp;<strong>encoding</strong>&nbsp;vs.&nbsp;<strong>hashing</strong>?</p>', '<p>Hashing&nbsp;transforms&nbsp;data&nbsp;into&nbsp;a&nbsp;fixed-size,&nbsp;irreversible&nbsp;value&nbsp;for&nbsp;integrity&nbsp;or&nbsp;verification,&nbsp;while&nbsp;encoding&nbsp;transforms&nbsp;data&nbsp;into&nbsp;a&nbsp;reversible&nbsp;format&nbsp;for&nbsp;safe&nbsp;storage&nbsp;or&nbsp;transmission.</p>', '', '{}', true, '2025-12-03 14:13:40.017483+00', '2025-12-16 12:12:02.072733+00');
INSERT INTO public.questions (id, question_type, practical, difficulty, topic, question, answer, quiz, match_keywords, is_active, created_at, updated_at) VALUES (73, 'multiple_choice', false, 'hard', 'NodeJS Event Loop', '<p>What&nbsp;is&nbsp;the&nbsp;<strong>Reactor&nbsp;Pattern</strong>?</p>', '<p>The&nbsp;Reactor&nbsp;Pattern&nbsp;is&nbsp;an&nbsp;architecture&nbsp;where&nbsp;a&nbsp;single&nbsp;thread&nbsp;(the&nbsp;reactor)&nbsp;waits&nbsp;for&nbsp;events&nbsp;(like&nbsp;incoming&nbsp;connections,&nbsp;data,&nbsp;timers,&nbsp;or&nbsp;file&nbsp;I/O)&nbsp;and&nbsp;dispatches&nbsp;them&nbsp;to&nbsp;handlers&nbsp;without&nbsp;blocking.</p><p>Instead&nbsp;of&nbsp;creating&nbsp;a&nbsp;thread&nbsp;per&nbsp;client,&nbsp;the&nbsp;reactor&nbsp;uses:</p><ul><li>Non-blocking&nbsp;I/O</li><li>Event&nbsp;demultiplexing&nbsp;(like&nbsp;epoll,&nbsp;kqueue,&nbsp;IOCP)</li><li>Callbacks&nbsp;/&nbsp;handlers&nbsp;that&nbsp;run&nbsp;when&nbsp;the&nbsp;data&nbsp;is&nbsp;ready</li><li>This&nbsp;lets&nbsp;one&nbsp;thread&nbsp;efficiently&nbsp;handle&nbsp;thousands&nbsp;of&nbsp;connections.</li></ul><p></p><p><strong>How&nbsp;it&nbsp;works</strong>:</p><ul><li>The&nbsp;Reactor&nbsp;sits&nbsp;in&nbsp;an&nbsp;event&nbsp;loop.</li><li>It&nbsp;listens&nbsp;for&nbsp;I/O&nbsp;events&nbsp;from&nbsp;many&nbsp;sources.</li><li>When&nbsp;an&nbsp;event&nbsp;occurs,&nbsp;it&nbsp;uses&nbsp;an&nbsp;OS&nbsp;mechanism&nbsp;to&nbsp;detect&nbsp;it.</li><li>It&nbsp;dispatches&nbsp;the&nbsp;event&nbsp;to&nbsp;the&nbsp;correct&nbsp;callback/handler.</li><li>The&nbsp;loop&nbsp;continues,&nbsp;never&nbsp;blocking.</li></ul><p></p><p><strong>Why&nbsp;it&nbsp;exists</strong>:</p><ul><li>Traditional&nbsp;server&nbsp;model:&nbsp;One&nbsp;thread&nbsp;per&nbsp;client&nbsp;→&nbsp;Too&nbsp;many&nbsp;threads&nbsp;→&nbsp;slow,&nbsp;expensive,&nbsp;memory-heavy</li><li>Reactor&nbsp;model:&nbsp;One&nbsp;thread&nbsp;handles&nbsp;many&nbsp;clients&nbsp;→&nbsp;Fast,&nbsp;scalable,&nbsp;non-blocking&nbsp;→&nbsp;Perfect&nbsp;for&nbsp;high-concurrency&nbsp;servers&nbsp;(Node,&nbsp;Nginx,&nbsp;Redis)</li></ul>', '<p>Which&nbsp;of&nbsp;the&nbsp;following&nbsp;are&nbsp;true&nbsp;about&nbsp;<strong>Reactor&nbsp;Pattern</strong>?</p>', '{}', true, '2025-12-05 12:43:41.730863+00', '2025-12-16 12:12:02.072733+00');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2904, 24, 'A Bounded Context is a clearly defined domain boundary within which a specific model, terminology, and set of rules are consistent and isolated from other parts of the system.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2905, 24, 'A Bounded Context is a microservice that contains all business logic for the entire application and coordinates other services.', false, 'Bounded contexts are conceptual domain boundaries, not single mega-services.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2906, 24, 'A Bounded Context is a shared global model used by all teams so everyone speaks the same ubiquitous language across the entire organization.', false, 'Bounded contexts intentionally limit a model to prevent universal models.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2907, 24, 'A Bounded Context is a database schema that enforces strict table separation but allows the same domain model to be reused everywhere.', false, 'Bounded contexts are not defined by database layouts and models are not reused across contexts.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2908, 24, 'A Bounded Context is a UI design pattern where each page contains its own isolated components to avoid mixing visual concerns.', false, 'UI separation is unrelated to DDD bounded contexts.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2909, 24, 'A Bounded Context is the practice of using a single entity definition in all subdomains so the system remains consistent.', false, 'Bounded contexts avoid unified entities across subdomains.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2910, 24, 'A Bounded Context is a code organization technique where classes are grouped alphabetically so developers can find them faster.', false, 'Alphabetical grouping has nothing to do with domain boundaries.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2911, 24, 'A Bounded Context means splitting domain logic evenly across all services so each service contains exactly the same version of the domain model.', false, 'Bounded contexts isolate models; they do not replicate them.');
