INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3168, 35, 'A type in TypeScript can describe unions, intersections, primitives, and object shapes.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3169, 35, 'An interface defines an object’s structure and can be extended or merged with other interfaces.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3170, 35, 'An abstract class can include both implemented methods and abstract methods that subclasses must override.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3171, 35, 'Both types and interfaces are erased at compile time and do not exist in the emitted JavaScript.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3172, 35, 'Abstract classes exist at runtime because they compile to JavaScript classes, unlike types and interfaces.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3173, 35, 'A type is always faster at runtime than an interface because TypeScript optimizes typed structures differently.', false, 'There is no runtime performance difference; both erase completely.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3174, 35, 'An interface cannot extend another interface, but it can extend a type containing a union of objects.', false, 'Interfaces can extend interfaces, but cannot extend unions.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3175, 35, 'An abstract class cannot contain a constructor because constructors break abstraction guarantees.', false, 'Abstract classes may have constructors.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3176, 35, 'A type automatically becomes an interface when used to describe an object, so interfaces are just a syntax shortcut.', false, 'Types and interfaces are distinct; no automatic conversion.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3177, 35, 'An interface can define method implementations as long as they do not reference instance fields.', false, 'Interfaces cannot contain implementations in TypeScript.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3208, 46, 'Decorators let you attach extra behavior or metadata to classes, methods, or properties.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3209, 46, 'They’re often used in frameworks to define routes, inject services, or modify behavior cleanly.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3210, 46, 'Decorators are great for cross-cutting concerns such as logging, validation, caching, or timing method calls.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3211, 46, 'A decorator receives information about the target it is applied to, allowing it to modify or wrap the target’s behavior.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3212, 46, 'Decorators are evaluated at design time and can be used to extend or augment class features before instances are created.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3213, 46, 'Decorators automatically wrap every function in asynchronous behavior, even if the function is synchronous.', false, 'Decorators do not change sync/async behavior unless explicitly coded.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3214, 46, 'Decorators permanently modify the JavaScript runtime environment, changing how classes work globally.', false, 'They operate only on the targets they decorate.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3215, 46, 'Decorators can only be applied to standalone functions and cannot be used on classes or class members.', false, 'Decorators apply to classes and class members, not standalone functions.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3216, 46, 'A decorator runs every time the decorated method is called, even if it was defined only on a class property.', false, 'Decorators run when the class is defined, not on every call.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3217, 46, 'Decorators eliminate the need for dependency injection since they automatically create and manage all instances.', false, 'Decorators may assist DI frameworks but do not replace DI.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3258, 52, 'JWT is a token format that defines how claims are encoded and signed, while OAuth2 is an authorization framework that defines how clients obtain and use access tokens, and can use many token formats including JWT.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3259, 52, 'JWT is a complete authentication protocol that replaces OAuth2 and handles login, consent, and authorization scopes on its own.', false, 'JWT is not a protocol; it’s only a token format. It does not define flows, login, or consent.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3260, 52, 'OAuth2 is simply a token format for embedding user data, while JWT is the system that provides authorization flows like authorization code or client credentials.', false, 'OAuth2 defines flows; JWT does not. OAuth2 is not a token format.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3261, 52, 'JWT always requires a centralized authorization server, while OAuth2 can operate without one.', false, 'OAuth2 requires an authorization server; JWT does not require any servers at all.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3262, 52, 'OAuth2 tokens must always be JWTs, because the framework mandates signed JSON tokens for every flow.', false, 'OAuth2 supports opaque tokens, random strings, or JWTs — it has no token-format requirement.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3355, 64, 'A reverse proxy hides backend servers from clients and routes incoming requests to the appropriate internal service.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2912, 25, 'The Saga Pattern is a way to manage distributed business transactions by executing a series of local operations in different services, each paired with a compensating action that can undo changes if a later step fails. Correct.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2913, 25, 'The Saga Pattern is a centralized workflow engine that locks all participating services until every operation finishes, ensuring full ACID guarantees across the system.', false, 'Sagas avoid central locking and do not provide ACID transactions.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2914, 25, 'The Saga Pattern requires all microservices to share the same database schema so compensating actions can be applied directly to the original tables.', false, 'Sagas exist because services do not share a database.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2915, 25, 'The Saga Pattern is a technique where a single global transaction manager coordinates distributed commits using two-phase commit.', false, 'Two-phase commit is the opposite of sagas.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2916, 25, 'The Saga Pattern guarantees real-time consistency by forcing all steps to complete before any service can proceed with other requests.', false, 'Sagas provide eventual consistency, not real-time blocking.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3263, 52, 'JWT is used only for refresh tokens, and OAuth2 is used only for access tokens.', false, 'JWTs can represent access tokens, ID tokens, or refresh tokens. OAuth2 does not enforce formats.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3264, 52, 'OAuth2 enforces a specific signing algorithm for all tokens, while JWT does not support signatures at all.', false, 'OAuth2 does not mandate algorithms. JWT supports signatures (HS256, RS256, ES256, etc.).');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2917, 25, 'The Saga Pattern is used to automatically discover which service should run next in a workflow by polling all services for their readiness.', false, 'Sagas follow a defined sequence or event choreography, not discovery polling.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2918, 25, 'The Saga Pattern works only in synchronous request-response architectures because compensations must be triggered immediately.', false, 'Sagas typically rely on asynchronous messaging.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2919, 25, 'The Saga Pattern is a refactoring technique that merges multiple microservices into one larger service to prevent distributed failure scenarios.', false, 'Sagas coordinate services, they do not merge them.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3265, 52, 'JWT automatically provides authorization and role management, while OAuth2 is only used to encrypt tokens.', false, 'JWT does not manage roles or authorization. OAuth2 is not about encryption; it defines flows.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3322, 62, 'The Saga pattern in DDD forces aggregates to expose their internal state directly so compensating actions can update them without going through domain rules.', false, 'Aggregates never expose internal state directly; Sagas interact via commands or events, respecting domain invariants.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3323, 62, 'DDD prohibits the use of Sagas because they introduce eventual consistency, which is incompatible with the strict transactional guarantees required in all bounded contexts.', false, 'DDD explicitly supports eventual consistency across contexts, and Sagas are a common pattern for handling exactly that.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3356, 64, 'A reverse proxy can improve scalability through load balancing across multiple backend servers.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3357, 64, 'A reverse proxy can handle SSL termination, offloading encryption work from backend services.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3358, 64, 'A reverse proxy is a client-side tool that rewrites outgoing HTTP requests before they reach the internet, used mostly in browsers for modifying URLs.', false, 'A reverse proxy operates server-side, handling incoming traffic to backend servers.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3359, 64, 'A reverse proxy stores full copies of backend applications and automatically updates them when clients send new requests.', false, 'Reverse proxies do not store application code; they forward requests.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3360, 64, 'A reverse proxy initiates outbound connections to fetch content on behalf of servers, functioning as a secure tunnel for server-to-client communication.', false, 'You’re describing a forward proxy or tunneling service; reverse proxies handle client-to-server traffic.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3361, 64, 'Reverse proxies are required only when using IPv6, because backend servers cannot communicate directly with IPv4 clients.', false, 'Reverse proxies have nothing to do with IPv4/IPv6 compatibility.');
