INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2920, 26, 'Event Sourcing is an architectural pattern in which all changes to an application''s state are stored as an immutable sequence of events, and the current state is derived by replaying those events.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3119, 28, 'Builder', false, 'It''s a creational design pattern.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2921, 26, 'Event Sourcing is an approach where events are used as notifications to trigger workflow steps, but the actual application state is stored only as the latest snapshot.', false, 'This describes event-driven workflow, not event sourcing.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2922, 26, 'Event Sourcing is a pattern in which services continuously publish their internal logs so that consumers can reconstruct state from those log streams.', false, 'Log streaming is not event sourcing.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2923, 26, 'Event Sourcing is a technique where events represent the final state of an entity, and each new event overwrites the previous one to simplify storage.', false, 'Events do not overwrite each other; they accumulate.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2924, 26, 'Event Sourcing is a data persistence model where the database stores commands instead of events, and the system rebuilds state by re-executing those commands.', false, 'Commands are not stored or replayed; events are.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2925, 26, 'Event Sourcing is a mechanism where every microservice maintains its own authoritative state and publishes updates so the rest of the system can merge the states into a unified view.', false, 'State-merging between services is not event sourcing.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2926, 26, 'Event Sourcing is a pattern in which all domain objects persist their current state directly, and events are generated only for auditing purposes.', false, 'In event sourcing, events are the source of truth, not audits.');
