INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2927, 26, 'Event Sourcing is a storage design where events are mutable records that can be corrected or rewritten to ensure the event stream always reflects the latest truth.', false, 'Events are immutable and never rewritten.');
