INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3178, 36, 'extends is used for inheritance: a class can extend another class and receive its behavior and structure.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3179, 36, 'When an interface extends another interface, it inherits its members and can add new ones.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3180, 36, 'implements is used when a class promises to satisfy the shape defined by an interface without inheriting any implementation.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (1538, 8, 'call invokes a function immediately with a specific this value and individual arguments, apply invokes it immediately with a specific this value and an array of arguments, and bind returns a new function with this fixed without invoking it.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (1539, 8, 'call runs the function immediately with a fixed this, while apply creates a new function with that this, and bind executes the function only after all arguments have been provided.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (1540, 8, 'apply is used for asynchronous invocations, call is used for synchronous ones, and bind is used when you want the function to ignore its original this context.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (1541, 8, 'bind works like call but executes the function only once and then discards it, while apply works like a version of call that always converts arguments into arrays.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (1542, 8, 'call permanently changes the this value of the target function, apply temporarily changes it, and bind allows switching between the two behaviors depending on arguments.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (1543, 8, 'apply and bind both return new functions with prefilled arguments, while call simply invokes the original function without modifying its this value.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (1544, 8, 'call is used when the function takes no parameters, apply is used when the function expects parameters, and bind is used only on arrow functions since they have lexical this.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (1545, 8, 'bind is just a shorthand for apply that always uses the first element of the arguments array as the function’s this, while call bypasses the need for arguments entirely.', false, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3266, 53, 'A JWT consists of three parts: a Header, a Payload, and a Signature.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3267, 53, 'JWTs are commonly used for authentication and authorization by verifying user identity and access rights.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3268, 53, 'A JWT is self-contained, meaning it includes all necessary claims without relying on server-side session storage.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3181, 36, 'A class can extend only one class, but it can implement multiple interfaces.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3182, 36, 'An interface can extend multiple interfaces, allowing composition of shapes.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3183, 36, 'implements copies all methods and properties from the parent automatically, giving the class full inherited behavior.', false, 'Implements enforces structure only; no behavior is inherited.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3184, 36, 'A class can extend multiple classes using extends, but it can implement only one interface.', false, 'Multiple inheritance is not allowed, but multiple interfaces are.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3185, 36, 'Interfaces cannot use extends because only classes are allowed to create inheritance hierarchies.', false, 'Interfaces can extend interfaces freely.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3186, 36, 'implements can be used by interfaces to inherit class behavior, making interfaces act like abstract classes.', false, 'Interfaces cannot implement anything and cannot inherit behavior.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3269, 53, 'The signature provides integrity, ensuring the token has not been -modified since it was issued.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3270, 53, 'JWT-based systems are stateless because the server only needs a secret or public key to verify incoming tokens.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3271, 53, 'A JWT stores user passwords in encrypted form inside the payload to allow servers to re-authenticate users.', false, 'Passwords are never stored in JWTs.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3272, 53, 'JWTs must always be encrypted, and unsigned tokens are invalid according to the standard.', false, 'JWTs are signed (JWS) by default; encryption (JWE) is optional.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3273, 53, 'The server must store every issued JWT in a database to validate whether it is still active.', false, 'JWT validation is stateless and does not require storage.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3274, 53, 'A JWT automatically expires as soon as the user logs out, even without server involvement.', false, 'JWTs remain valid until expiration unless explicitly revoked.');
