INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3223, 48, 'Hexagonal Architecture requires using hexagon-shaped folder structures and enforces six separate modules for every application.', false, 'The “hexagon” is metaphorical, not literal.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3224, 48, 'Clean Architecture depends on specific frameworks such as NestJS or Spring, while Hexagonal Architecture does not.', false, 'Both are framework-agnostic.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3225, 48, 'Hexagonal Architecture discourages the use of ports and adapters, instead recommending direct calls to external services.', false, 'Ports and adapters are its core mechanism.');
