INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3280, 54, 'The JWT signature allows clients to modify claims safely, because the signature will regenerate itself.', false, 'Clients cannot modify claims; doing so invalidates the signature.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3281, 54, 'The JWT signature determines the user’s role and permissions inside the application.', false, 'Roles are part of the payload, not the signature.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3282, 54, 'The JWT signature hides sensitive fields like email or ID so attackers cannot decode them.', false, 'JWT payloads are not hidden or protected by signatures.');
