INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3324, 63, 'TCP guarantees ordered, reliable delivery using acknowledgements and retransmission mechanisms.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3325, 63, 'UDP is connectionless and does not require a handshake, enabling faster transmission with lower overhead.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3326, 63, 'UDP is suitable for real-time use cases (gaming, VoIP, streaming) where occasional packet loss is acceptable but latency must be minimal.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3327, 63, 'TCP and UDP both use a handshake process to establish a stable, connection-oriented session before sending any data.', false, 'Only TCP uses a handshake; UDP does not establish a connection.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3328, 63, 'UDP guarantees delivery by storing packets until acknowledgements are received, ensuring no data is lost.', false, 'UDP provides no acknowledgements, no retransmissions, and no delivery guarantees.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3329, 63, 'TCP is faster than UDP because it avoids overhead and skips retransmission logic to deliver packets immediately.', false, 'TCP is slower due to reliability mechanisms; UDP is the lightweight option.');
