INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3331, 63, 'TCP is typically used for real-time applications like gaming and live voice because its reliability prevents delay.', false, 'TCP’s retransmissions create latency; real-time apps usually use UDP.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3332, 63, 'UDP is a stateful protocol that tracks session metadata for each packet exchange, while TCP is stateless.', false, 'TCP is stateful; UDP is stateless.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3333, 63, 'TCP supports broadcast and multicast communication, allowing data to reach multiple recipients, while UDP cannot.', false, 'UDP can broadcast/multicast; TCP cannot.');
