INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3344, 65, 'Node.js applications often use in-memory caches (e.g., Map, LRU cache libraries) for fast, process-local lookups.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3345, 65, 'External cache systems like Redis or Memcached allow sharing cached data across multiple Node.js instances.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3346, 65, 'Caching reduces expensive repeated operations, improving throughput and lowering response times.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3347, 65, 'Node.js caches modules after the first require/import, returning the same instance on subsequent loads and effectively making modules singletons.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3348, 65, 'Node.js caching automatically persists all cached values to disk without any configuration, guaranteeing data survival across restarts.', false, 'Node.js does not persist cache automatically; in-memory caches disappear unless explicitly saved.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3349, 65, 'Using caching in Node.js removes the need for a database entirely, since cached values permanently replace backend data sources.', false, 'Caches are temporary and cannot replace durable databases.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3350, 65, 'Node.js requires built-in native caching APIs to perform any caching, because JavaScript objects cannot store key-value pairs efficiently.', false, 'Node.js has no native cache API, and JavaScript objects/Maps are already suitable for caching.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3351, 65, 'Every Node.js worker thread automatically shares the same in-memory cache, ensuring perfect consistency across the application.', false, 'Each Node.js process or worker maintains its own memory; caches are not shared unless using external stores like Redis.');
