INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3364, 64, 'A reverse proxy must run on the same host as the backend service to ensure minimal latency and direct file access.', false, 'Reverse proxies commonly run on separate machines.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3367, 66, 'A master process can distribute incoming connections across workers, improving scalability and throughput.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3368, 66, 'Workers can exit or crash independently, and the master can respawn them to maintain service availability.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3369, 66, 'Node.js clustering merges multiple CPU cores into a single event loop, allowing one process to run code in true parallel threads.', false, 'Clustering creates multiple processes, not one multi-threaded event loop.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3370, 66, 'Clustering automatically shares in-memory caches between workers, guaranteeing consistent state across processes.', false, 'Workers have isolated memory; shared state requires Redis or another external store.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3371, 66, 'A cluster worker can access another worker’s variables directly, since all workers run inside the same memory space.', false, 'Workers cannot access each other’s memory; IPC messaging or shared external systems are required.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3372, 66, 'Clustering eliminates the need for load balancers, because it prevents any worker from becoming overloaded.', false, 'Clustering helps, but cannot replace external load balancers in production architectures.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3373, 66, 'Node.js clustering allows workers to execute synchronous CPU-bound operations without impacting overall performance.', false, 'CPU-bound operations still block each worker’s event loop; clustering just spreads load across processes.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3374, 66, 'Cluster workers always restart automatically when they exit, without requiring any configuration in the master process.', false, 'The master must explicitly respawn workers or handle the exit event to restart them.');
