INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3381, 67, 'Cookies in Node.js are stored in a built-in server-side cookie database that persists values across restarts.', false, 'Node.js has no built-in cookie storage; cookies live in the browser, not on the server.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3387, 68, 'Minor GC operates on the young generation and efficiently removes short-lived objects with minimal pause time.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3388, 68, 'Objects that survive several Minor GC cycles can be promoted to the old generation, which is handled by Major GC.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3389, 68, 'Major GC uses algorithms like mark-sweep and mark-compact to reclaim memory and reduce fragmentation.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3390, 68, 'V8 uses incremental marking so garbage collection work is split into smaller chunks, reducing long event-loop pauses.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3391, 68, 'GC in V8 is triggered automatically based on allocation thresholds, memory pressure, and heuristics—not manual intervention.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3392, 68, 'Major GC is responsible for cleaning the young generation, while Minor GC frees old-generation memory to avoid long compaction cycles.', false, 'It is the opposite—Minor GC handles the young generation, Major GC the old generation.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2822, 14, 'Immutable functions naturally support safe undo, redo, and state history because previous versions remain intact.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3393, 68, 'Objects are promoted to the old generation immediately after their first Minor GC cycle to reduce young-generation scanning.', false, 'Promotion happens only after multiple survivals, not after one Minor cycle.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3394, 68, 'Incremental GC eliminates all garbage-collection pauses entirely by performing every marking and sweeping operation concurrently with JavaScript execution.', false, 'Incremental GC reduces pauses but cannot remove them completely.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3395, 68, 'The Node.js event loop halts entirely during every step of GC, because V8 cannot interleave JavaScript execution with garbage-collection work.', false, 'Incremental and concurrent GC allow the event loop to run between GC slices.');
