INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2760, 6, 'If a Promise is resolved with another Promise, it will adopt the state of that inner Promise, effectively “flattening” the result.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2761, 6, 'Once a Promise is rejected, .then() handlers will still run automatically unless you explicitly call .catch().', false, 'Rejected promises skip .then() success handlers.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2762, 6, 'A Promise can resolve multiple times as long as the previous resolution was a primitive value, not an object.', false, 'Can settle only once.');
