INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2776, 9, 'Function declarations are hoisted with both their name and full body available before the point where they appear in the code.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2777, 9, 'Variable declarations made with var are hoisted, but their values are not assigned until execution reaches the assignment line.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2778, 9, 'let and const declarations are hoisted, but accessing them before initialization results in a temporal dead zone error.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2779, 9, 'A variable declared with var inside a function is hoisted to the top of that function’s scope, not to the global scope.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2780, 9, 'Only declarations are hoisted, not assignments, which means that referencing a var variable before it is assigned yields undefined.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2781, 9, 'let and const are not hoisted at all, and therefore they behave like undeclared variables until the moment execution reaches their declaration.', false, 'They are hoisted but uninitialized and cause a temporal dead zone error.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2782, 9, 'Function expressions are hoisted the same way as function declarations, so the function can be called before the line where it is defined.', false, 'Only the variable is hoisted, not the assigned function.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2783, 9, 'Using var inside a block such as an if statement hoists it only inside that block and not the surrounding function.', false, 'Var is function scoped, not block scoped.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2784, 9, 'Class declarations are hoisted with full initialization, which allows a class to be instantiated before its definition appears.', false, 'Class declarations are hoisted but uninitialized, similar to let and const, so they cannot be used before declaration.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2785, 9, 'Hoisting is controlled by strict mode, so disabling strict mode prevents hoisting entirely.', false, 'Hoisting is part of JavaScript’s compilation step and happens regardless of strict mode.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2786, 10, 'A closure is created when an inner function captures variables from its outer function’s scope.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2787, 10, 'Closures allow a function to access variables that were in scope at the time the function was defined, even after the outer function has finished executing.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2788, 10, 'Closures can be used to emulate private variables by keeping data accessible only to the inner function.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2789, 10, 'In JavaScript, every function naturally forms a closure over its lexical scope, even if the closure is never used.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2790, 10, 'Closures preserve references to variables, not copies of their values, so changes to those variables are visible inside the closure.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2791, 10, 'A closure only works if the inner function is returned from the outer function; otherwise no closure is formed.', false, 'Closures exist whenever a function references outer variables, return is not required.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2792, 10, 'Closures copy the values of outer variables at the moment the inner function is created, so they cannot observe later updates to those variables.', false, 'Closures hold references, not snapshots.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2793, 10, 'Closures can only capture variables declared with var, not with let or const. Incorrect: closures work with any lexical variable regardless of keyword.', false, 'Closures work with any lexical variable regardless of keyword.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2794, 10, 'Closures are disabled when using strict mode because strict mode isolates scope to prevent accidental variable sharing.', false, 'Strict mode does not disable closures at all.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2795, 10, 'A closure is destroyed as soon as the outer function finishes running, which means the inner function cannot access the outer variables anymore.', false, 'Closures exist specifically to keep those variables alive after the outer function returns.');
