INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2811, 13, 'Pure functions are naturally easier to test because their behavior is predictable and isolated.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2812, 13, 'In functional programming, pure functions allow safe function composition without unexpected interactions.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2813, 13, 'A function is pure as long as it does not modify global variables, even if it reads from them to compute its return value.', false, 'Reading mutable external state breaks purity.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2814, 13, 'Logging to the console is not considered a side effect, so functions that log output can still be pure.', false, 'Console output is a side effect.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2815, 13, 'A pure function may return different values if it uses a random number generator, as long as it does not modify external state.', false, 'Randomness breaks referential transparency.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2816, 13, 'Pure functions can freely mutate their arguments as long as the mutation happens only inside the function.', false, 'Mutating arguments is a side effect.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2817, 13, 'A pure function can call impure functions as long as it returns a value rather than modifying external state.', false, 'Calling an impure function makes the caller impure.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2818, 14, 'An immutable function does not modify the objects, arrays, or data structures passed into it.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2819, 14, 'Immutable functions return new values or new data structures instead of changing existing ones.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2820, 14, 'Immutable functions help avoid shared-state bugs because the original data remains unchanged.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2821, 14, 'Immutable functions often use shallow or deep copies to produce new versions of data.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2823, 14, 'A function is immutable as long as it does not modify global variables, even if it mutates the object given to it as an argument.', false, 'Mutating inputs breaks immutability.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2824, 14, 'Immutable functions never allocate new memory since they reuse the same data structures to avoid duplication.', false, 'They typically allocate new memory for new versions.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2825, 14, 'An immutable function can freely mutate nested properties of an object as long as the top-level reference stays the same.', false, 'Immutability requires not mutating any part of the data.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2826, 14, 'A function that mutates an internal variable is still considered immutable as long as callers cannot see that mutation.', false, 'Hidden internal mutation still breaks immutability.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2827, 14, 'Immutable functions require all inputs to be primitive values, since reference types cannot be handled immutably.', false, 'Immutable functions can work with all data types by cloning or copying.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2838, 18, 'Keeping responsibilities isolated makes code easier to maintain because each part has a clear purpose.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2839, 18, 'When components handle different responsibilities, they become easier to test in isolation.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2840, 18, 'Isolating responsibilities reduces unexpected side effects since changes affect only a limited area.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2841, 18, 'Code becomes more readable when unrelated logic is not mixed together.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2842, 18, 'Systems scale better when different parts can evolve independently without breaking others.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2843, 18, 'Dividing code by responsibility guarantees faster runtime performance because each unit runs on its own CPU thread.', false, 'Structural separation does not create threads.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2844, 18, 'When responsibilities are separated, documentation is no longer needed because the code becomes fully self-explanatory.', false, 'Documentation is still important.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2845, 18, 'Splitting responsibilities automatically removes all code duplication.', false, 'Duplication can still exist.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2846, 18, 'Each function must live in its own file to avoid mixing responsibilities.', false, 'Physical file structure is not dictated by responsibility separation.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2847, 18, 'Teams never experience merge conflicts when responsibilities are isolated.', false, 'Merge conflicts can still occur.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2848, 19, 'Dependency Injection is a design technique where required dependencies are provided to a component from the outside rather than created internally, making the component easier to test and maintain.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2849, 19, 'Dependency Injection is a pattern where objects automatically scan their environment to discover and attach the dependencies they need at runtime.', false, 'Components do not self-discover dependencies.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2850, 19, 'Dependency Injection means each component stores its own dependencies internally so they never need to be passed from the outside.', false, 'DI does the opposite of internalizing dependencies.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2851, 19, 'Dependency Injection is a process where all dependencies are globally shared singletons injected into any class that requests them.', false, 'DI does not require global singletons.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2852, 19, 'Dependency Injection forces all dependencies to be created lazily, ensuring objects are only constructed when first used.', false, 'DI does not dictate lazy or eager creation.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2853, 19, 'Dependency Injection is the mechanism where a class inherits all needed services from a base class instead of receiving them from constructors or setters.', false, 'Inheritance is not DI.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2854, 19, 'Dependency Injection refers to the practice of exposing all class internals to the dependency container so they can be modified dynamically during execution.', false, 'DI does not expose or mutate internals.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2855, 19, 'Dependency Injection is a technique where dependencies are bundled directly inside the module during build time, eliminating the need for runtime configuration.', false, 'That describes static linking or compile-time wiring, not DI.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2856, 20, 'Inversion of Control is a design principle where the flow of a program is delegated to an external framework or container, rather than being controlled directly by the application code.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2857, 20, 'Inversion of Control means that a class inherits its behavior from a parent class so the parent controls all lower-level logic.', false, 'Inheritance is unrelated to IoC.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3480, 73, 'A Reactor manages many concurrent connections on a single thread by invoking handlers when events become ready rather than spinning up threads.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2858, 20, 'Inversion of Control is the practice of reversing function arguments so that dependencies are passed last instead of first.', false, 'IoC has nothing to do with argument ordering.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2859, 20, 'Inversion of Control refers to dynamically swapping the roles of caller and callee at runtime, allowing methods to call their parents instead of children.', false, 'IoC is not about call-stack reversal.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2860, 20, 'Inversion of Control ensures that all business logic is executed inside constructors so that no external framework can manipulate program flow.', false, 'IoC typically reduces constructor-driven logic, not increases it.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2861, 20, 'Inversion of Control is achieved when a module retrieves its required dependencies from a global registry instead of receiving them from outside.', false, 'IoC avoids self-fetching; that violates DI and IoC.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2862, 20, 'Inversion of Control is the process by which lower-level code determines how higher-level modules should behave, making high-level components dependent on implementation details.', false, 'IoC is the opposite, giving high-level policies control.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2863, 20, 'Inversion of Control forces all application entry points to be placed in the deepest layers of the code so inner modules can orchestrate outer modules.', false, 'IoC does not invert layering or entry-point structure.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2864, 21, 'Orchestration refers to the automated coordination and management of multiple services or components so they work together in a defined, controlled sequence.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2865, 21, 'Orchestration means allowing each microservice to decide independently when and how to call other services without any central coordination.', false, 'That describes choreography, not orchestration.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2866, 21, 'Orchestration is the practice of embedding business logic directly inside infrastructure tools so deployment rules control application behavior.', false, 'Infrastructure does not own business logic.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2867, 21, 'Orchestration refers to running all services on the same server so they can communicate faster without network hops.', false, 'Co-locating services is not orchestration.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2868, 21, 'Orchestration is the process of splitting a large service into smaller unrelated services without defining how they interact.', false, 'That describes decomposition, not orchestration.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2869, 21, 'Orchestration means exposing every internal service call directly to clients so the client determines the order of execution.', false, 'Orchestration hides internal flows from clients.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2870, 21, 'Orchestration is when a system randomly chooses which service to call next based on runtime performance instead of following a predefined flow.', false, 'Orchestration is controlled, not random.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2871, 21, 'Orchestration is a technique where each component polls all other components for status updates until a consensus is reached.', false, 'Polling and consensus algorithms are unrelated to orchestration.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3316, 62, 'In DDD-based systems with multiple bounded contexts, the Saga pattern coordinates long-running or distributed business transactions by orchestrating domain events or commands across services, ensuring eventual consistency where a single ACID transaction is not possible.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3317, 62, 'The Saga pattern replaces aggregates in DDD entirely, allowing domain models to be discarded in favor of centralized transaction controllers.', false, 'Sagas do not replace aggregates; they coordinate processes between aggregates or bounded contexts.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3318, 62, 'DDD requires every bounded context to include exactly one Saga, making Sagas the mandatory entry point for all commands in the domain layer.', false, 'DDD does not mandate Sagas; they are optional and used only for cross-context, long-running workflows.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3319, 62, 'The Saga pattern eliminates the need for domain events in DDD because it forces all services to share a single transactional database.', false, 'Sagas commonly use domain events, and they explicitly support distributed systems—not shared databases.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3320, 62, 'In DDD, Sagas act as repositories that store the full history of an aggregate’s state, allowing developers to rebuild domain objects without event sourcing.', false, 'Sagas are process managers, not state stores; they do not function as repositories or event stores.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3321, 62, 'Sagas are used in DDD to merge multiple bounded contexts into a single context, removing boundaries so transactions become simpler and fully synchronous.', false, 'Sagas preserve boundaries and handle coordination; they do not merge bounded contexts.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2880, 22, 'Choreography is a decentralized interaction pattern where each service reacts to events and decides its own actions without a central coordinator controlling the overall flow.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2881, 22, 'Choreography is the process of having a single central orchestrator dictate how all services interact in a predefined sequence.', false, 'That describes orchestration.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2882, 22, 'Choreography means running all microservices in strict alphabetical order so the system produces predictable results.', false, 'Service names have nothing to do with execution flow.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2883, 22, 'Choreography requires each service to expose its internal logic to all other services so they can directly coordinate behavior.', false, 'Choreography is event-driven, not internal-logic sharing.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2884, 22, 'Choreography is when a system routes all requests through a gateway that decides which service to invoke next.', false, 'This is centralized routing, not decentralized coordination.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2885, 22, 'Choreography forces services to communicate through a shared global state that all services read and write to in order to coordinate behavior.', false, 'Shared mutable state is not choreography.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2886, 22, 'Choreography is the practice of converting synchronous APIs into asynchronous ones by wrapping every call in a queue.', false, 'Asynchronous communication is common in choreography but not its definition.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2887, 22, 'Choreography refers to combining multiple unrelated services into a single deployment package so they can coordinate more efficiently.', false, 'Packaging services together is unrelated to choreography.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3198, 47, 'It places business logic at the center so that the most important rules of the system are isolated from external concerns.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3199, 47, 'Frameworks, databases, UI, and external systems belong to outer layers that depend inward on the core logic.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3200, 47, 'Dependencies always point toward the business rules, meaning inner layers never rely on outer layers.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3201, 47, 'It aims for testability, independence from specific frameworks or databases, and the ability to swap components such as REST vs GraphQL or MongoDB vs Postgres.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3202, 47, 'Its purpose is long-term maintainability by preventing technical details from leaking into business logic.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2896, 23, 'Event-driven architecture is a design style where components communicate by producing and reacting to events, resulting in loosely coupled and asynchronous interactions.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2897, 23, 'Event-driven architecture requires a central orchestrator that dictates the order in which all events must be processed.', false, 'EDA is decentralized, not orchestrated.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2898, 23, 'Event-driven architecture means every component must broadcast its internal state to all other components in real time.', false, 'EDA publishes events, not full internal state.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2899, 23, 'Event-driven architecture ensures that all components execute in a single thread so event ordering is guaranteed and deterministic.', false, 'EDA does not enforce single-threading or total ordering.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2900, 23, 'Event-driven architecture forces components to wait synchronously for event handlers to respond before continuing execution.', false, 'EDA is typically asynchronous and non-blocking.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2901, 23, 'Event-driven architecture eliminates the need for any persistence layer because events themselves are stored as the full source of truth.', false, 'Event storage varies; EDA does not remove databases.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2902, 23, 'Event-driven architecture requires each component to poll every other component periodically to discover new events.', false, 'Events are pushed or published, not discovered via polling.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (2903, 23, 'Event-driven architecture is simply another term for deploying all services together in a single monolith so communication happens through in-process calls.', false, 'EDA is about loose coupling, not monolithic deployment.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3203, 47, 'Clean Architecture requires every project to use microservices so each layer is deployed as a separate service.', false, 'Deployment style is unrelated.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3204, 47, 'Clean Architecture means placing all business logic inside the framework layer to maximize integration with controllers and routers.', false, 'Frameworks should not contain business rules.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3205, 47, 'Clean Architecture enforces that every outer layer must also depend on every other outer layer before reaching the core.', false, 'Outer layers should not depend on each other.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3206, 47, 'Clean Architecture requires storing all state in a single shared database so inner and outer layers can access it directly.', false, 'Database is an outer detail and should not be accessed directly by inner layers.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3207, 47, 'Clean Architecture mandates specific tools and frameworks such as NestJS or Spring, without which the architecture cannot be implemented.', false, 'It is tool-agnostic.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3248, 51, 'A microservice architecture breaks a system into small, independently deployable services that each handle a specific business capability.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3250, 51, 'Services communicate with each other through lightweight mechanisms such as HTTP or messaging.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3251, 51, 'Microservices allow teams to deploy, scale, and release each service independently.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3252, 51, 'The architecture reduces coupling by ensuring that services do not share the same codebase or database tables.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3253, 51, 'Microservices require all services to use the same programming language and framework to ensure consistency.', false, 'Services can use different languages and frameworks.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3254, 51, 'A microservice architecture mandates a single centralized database shared by all services for easier management.', false, 'Services should not share a single database schema.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3255, 51, 'Microservices must be deployed on separate physical servers; running them on the same machine breaks the architecture.', false, 'Physical deployment is irrelevant.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3256, 51, 'Communication between microservices must always be synchronous to keep the system consistent.', false, 'Asynchronous messaging is common and often preferred.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3257, 51, 'Microservices eliminate the need for APIs since each service directly accesses the internal logic of other services.', false, 'Services never access each other’s internals; they communicate only through APIs/messages.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3382, 67, 'Setting a cookie in Node.js requires a persistent WebSocket connection so the server can transmit cookie metadata.', false, 'Cookies are transmitted via standard HTTP headers, not WebSockets.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3383, 67, 'Node.js merges cookies from all clients into one shared memory object for easier global access.', false, 'Each request carries its own cookie header; no global cookie state exists.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3384, 67, 'Express automatically deletes expired cookies from the browser without needing any configuration.', false, 'Browsers handle expiration; the server cannot delete a cookie except by setting a new one with an expired date.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3385, 67, 'Cookies must be JSON-encoded objects in Node.js because plain string values are not supported by HTTP.', false, 'Cookies are simple strings; JSON is optional and often encoded manually.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3386, 67, 'Node.js can only send one cookie per response because multiple Set-Cookie headers will overwrite each other.', false, 'HTTP explicitly allows multiple Set-Cookie headers, each defining a separate cookie.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3397, 69, 'The browser event loop is integrated with Web APIs and the rendering pipeline, while Node.js relies on libuv to provide event-loop phases for timers, I/O, poll, and check.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3398, 69, 'Both the browser and Node.js use V8’s microtask queue semantics, meaning microtasks run before the next macrotask regardless of environment.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3399, 69, 'Node.js event loop contains multiple structured phases (timers, pending callbacks, poll, check, close callbacks) that do not exist in browser environments.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3400, 69, 'Browser event loops include a rendering step, whereas Node.js does not perform rendering and therefore has no rendering phase.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3401, 69, 'Node.js adds system-level capabilities (file I/O, TCP, DNS, etc.) to the event loop through libuv, while browsers delegate such operations to Web APIs or do not support them.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3402, 69, 'Browsers run microtasks only after rendering completes, while Node.js runs microtasks only before rendering begins.', false, 'Browsers and Node.js both run microtasks before the next macrotask; rendering is unrelated in Node.js.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3403, 69, 'The Node.js event loop is single-phased and strictly identical to the browser event loop, differing only in available global objects.', false, 'Node.js has multiple libuv phases not found in browsers.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3404, 69, 'Browser event loops allow JavaScript to perform synchronous filesystem I/O during the poll phase, but Node.js forbids all filesystem access inside the event loop.', false, 'Browsers cannot access the filesystem synchronously at all; Node.js can, but it blocks the event loop.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3405, 69, 'Node.js executes timers (setTimeout, setInterval) strictly at the scheduled millisecond mark, while browsers may delay timers but Node never does.', false, 'Both environments provide best-effort timing; neither guarantees exact millisecond accuracy.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3481, 73, 'The Reactor pattern separates event detection from event handling, allowing the event loop to multiplex many I/O sources efficiently.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3406, 69, 'In browsers, microtasks can be postponed until after several macrotasks, while Node.js forces microtasks to run only at the end of the poll phase.', false, 'Microtasks run immediately after the current operation, in both environments, not arbitrarily postponed.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3411, 70, 'libuv enables Node.js to perform asynchronous operations even when the underlying operating system only supports synchronous APIs.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3412, 70, 'libuv executes all JavaScript code inside its worker threads, allowing Node.js to run JavaScript in parallel by default.', false, 'JavaScript runs on the main V8 thread; worker threads are used only for specific async tasks.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3413, 70, 'libuv provides a built-in garbage collector that works alongside V8 to manage memory and clean unreachable objects.', false, 'Garbage collection is handled entirely by V8, not libuv.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3414, 70, 'libuv exposes its API directly to JavaScript code, so developers can call libuv functions without any native bindings.', false, 'libuv APIs are not exposed to JavaScript; Node’s native layer wraps them.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3415, 70, 'Major GC in V8 is triggered by libuv scheduling it as part of the event loop’s poll phase.', false, 'GC is handled by V8, not orchestrated by libuv.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3416, 70, 'libuv creates a separate event loop instance for every Node.js module loaded via require/import.', false, 'A Node.js process has one primary event loop, not one per module.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3417, 71, 'process.nextTick() is a Node.js mechanism that schedules a callback to run immediately after the current JavaScript execution stack completes, before the event loop proceeds to the next phase, effectively giving it priority over other queued work.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3418, 71, 'process.nextTick() is a scheduling API that defers a callback to the next event-loop phase after all macrotasks and microtasks have been processed, ensuring it runs at the lowest priority.', false, 'nextTick runs before the event loop advances, not after everything else.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3419, 71, 'process.nextTick() is a Node.js timer primitive that guarantees a callback executes after a minimum delay determined by the runtime’s internal scheduling interval.', false, 'nextTick is not time-based and introduces no delay.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3420, 71, 'process.nextTick() is a mechanism that queues a callback for execution exclusively during the poll phase, ensuring it runs after all pending I/O operations.', false, 'nextTick executes before entering the next phase, not specifically during poll.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3421, 71, 'process.nextTick() is a concurrency construct that dispatches callbacks to worker threads so they run independently of the main event loop.', false, 'nextTick callbacks execute on the main thread, not worker threads.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3422, 71, 'process.nextTick() is a browser-level microtask API standardized for Web APIs, enabling consistent scheduling behavior across client-side JavaScript environments.', false, 'nextTick is Node.js-only, not a browser or standard API.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3423, 71, 'process.nextTick() is a memory-management hook that defers execution of a callback until after V8 completes its next garbage-collection cycle.', false, 'nextTick has no relation to GC cycles.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3424, 71, 'process.nextTick() is a startup routine that runs callbacks once during process initialization, before user scripts and modules are executed.', false, 'nextTick works throughout the program lifecycle, not only at startup.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3425, 72, 'Microtasks are executed immediately after the current call stack clears and before the event loop proceeds to the next macrotask.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3426, 72, 'Macrotasks represent operations such as timers and I/O events that execute one at a time in successive event-loop iterations.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3427, 72, 'Promise resolution handlers run as microtasks, giving them higher scheduling priority than timer callbacks.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3428, 72, 'A macrotask will not begin execution until the microtask queue has been fully drained for that iteration of the event loop.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3429, 72, 'Microtask execution ordering is deterministic: all queued microtasks are processed before the next macrotask is started.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3430, 72, 'Microtasks execute only after all macrotasks and rendering steps have completed, giving them the lowest scheduling priority in the event loop.', false, 'Microtasks run before the next macrotask, not after everything else.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3431, 72, 'Macrotasks include operations such as promise callbacks and mutation observer notifications, which require immediate post-stack execution.', false, 'Promise callbacks and mutation observers are microtasks, not macrotasks.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3432, 72, 'Microtasks are scheduled to run only during the poll phase of the event loop and cannot execute in other phases.', false, 'Microtasks run after the current stack, independent of phases.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3433, 72, 'Macrotasks must complete before any microtask can be scheduled, preventing microtasks from accumulating during synchronous execution.', false, 'Microtasks can be queued any time, including during synchronous code.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3434, 72, 'A microtask may be postponed across multiple macrotask cycles if the runtime determines that postponement improves throughput.', false, 'Microtasks run immediately before the next macrotask, not postponed arbitrarily.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3471, 34, 'Hashing transforms data into a fixed-size, irreversible value for integrity or verification, while encoding transforms data into a reversible format for safe storage or transmission.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3472, 34, 'Hashing is a reversible transformation used to compress data, while encoding is an irreversible transformation intended to protect sensitive information.', false, 'Hashing is irreversible; encoding is reversible.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3473, 34, 'Hashing converts text into an encrypted cipher, while encoding converts text into a hashing algorithm for secure comparison.', false, 'Hashing is not encryption and encoding is not hashing.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3474, 34, 'Hashing is used to convert binary data into readable text like Base64, while encoding is used to securely store passwords.', false, 'Base64 is encoding; passwords should be hashed.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3475, 34, 'Hashing guarantees data confidentiality by hiding the original content, while encoding ensures that two different inputs always result in the same 	encoded output.', false, 'Hashing does not guarantee confidentiality, and encoding is not collision-producing.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3476, 34, 'Hashing transforms data so it can be easily restored later, while encoding randomizes data so it cannot be returned to its original form.', false, 'The opposite of both is true.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3477, 34, 'Hashing and encoding both serve the same purpose: to encrypt data using specialized algorithms for secure transmission.', false, 'Neither hashing nor encoding is encryption.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3478, 34, 'Hashing changes the structure of text so browsers can render it correctly, while encoding ensures password checks remain deterministic and unforgeable.', false, 'Browser-safe formats are encoding; password verification uses hashing.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3479, 73, 'The Reactor pattern uses a non-blocking event demultiplexer (such as epoll, kqueue, or IOCP) to wait for I/O readiness and dispatch events to handlers.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3482, 73, 'Network servers like Node.js (via libuv) and Nginx use the Reactor pattern to achieve scalable, asynchronous I/O without blocking.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3483, 73, 'The Reactor pattern handles readiness-based events (e.g., readable or writable sockets), invoking callbacks only when operations can proceed without blocking.', true, NULL);
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3484, 73, 'The Reactor pattern spawns a new operating-system thread for every incoming connection, allowing each handler to execute independently.', false, 'Reactor avoids thread-per-connection; it relies on a single-threaded event loop.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3485, 73, 'The Reactor pattern processes all I/O synchronously, blocking until each handler finishes before checking for new events.', false, 'Reactor is based on non-blocking I/O and asynchronous dispatch.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3486, 73, 'The Reactor pattern relies on polling all file descriptors in a tight loop without waiting for readiness notifications.', false, 'Reactor uses an event demultiplexer, not busy-polling.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3487, 73, 'In the Reactor pattern, handlers initiate I/O directly without the reactor’s involvement, making the event loop optional.', false, 'The reactor coordinates readiness and dispatch; it is the core of the pattern.');
INSERT INTO public.choices (id, question_id, choice_text, is_good, explanation) VALUES (3488, 73, 'The Reactor pattern guarantees that each event handler runs in parallel across multiple CPU cores by default.', false, 'Reactor is typically single-threaded; parallelism requires additional patterns (e.g., thread pools, worker threads).');
